import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ArrowRight } from "react-feather";
import bg from "../images/bg2.jpg";
import { Spin } from "antd";
import { LoadingOutlined } from "@ant-design/icons";
import { useForm } from "react-hook-form";

// ReactDOM.render(<Spin indicator={antIcon} />, mountNode);

const Home = () => {
  const [loading, setloading] = useState(false);

  const [step, setstep] = useState(initialState);

  const antIcon = (
    <LoadingOutlined style={{ fontSize: 25, color: "white" }} spin />
  );

  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    console.log(data);
    setloading(true);
  };

  return (
    <>
      <div className="parent">
        <nav></nav>
        <div
          className="intro-banner"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),url(${bg})`,
            backgroundRepeat: "no-repeat",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        >
          <section className="quote-section">
            <h3>
              <span>Save upto 80% on Car Insurance.</span>
            </h3>
            <div className="quote-form">
              <h6>
                I am looking for Car Insurance <Link>Need help?</Link>
              </h6>
              <form
                className="number-plate-box"
                onSubmit={handleSubmit(onSubmit)}
              >
                <input
                  type="text"
                  name="number_plate"
                  placeholder="Enter Number Plate (KXX XXX)"
                  disabled={loading ? true : false}
                  ref={register({
                    required: "Please enter number plate",
                    // pattern: {
                    //   value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    //   message: "invalid number plate",
                    // },
                  })}
                />
                <button type="submit" disabled={loading ? true : false}>
                  {loading ? <Spin indicator={antIcon} /> : "Get Quotes"}
                </button>
              </form>
              <span className="err_msg">
                {errors.number_plate && errors.number_plate.message}
              </span>
              <hr />
              <div className="no-number-plate-box">
                <span>OR</span>
                <button>
                  Proceed without Number Plate&nbsp;
                  <ArrowRight size={15} />
                </button>
              </div>
            </div>
          </section>
        </div>
      </div>
    </>
  );
};

export default Home;
