import React, { useState, useContext, useEffect } from "react";
import { GlobalContext } from "../context/GlobalState";
import { Link } from "react-router-dom";
import {
  Check,
  ChevronDown,
  X,
  Star,
  HelpCircle,
  CornerUpLeft,
} from "react-feather";
import Navigation from "../components/layout/Navigation";
import Modal from "../components/modals/Modal";
import apa from "../images/logos/apa.png";
import { decryptData } from "../helpers/Functions";
import { plans } from "../data";

const Quote = (props) => {
  const [activeAccordion, setactiveAccordion] = useState(1);

  const { getCarInfo, excess, pvt, aa_rescue, phcf, car_info } =
    useContext(GlobalContext);

  const [plan_info, setplan_info] = useState(null);

  const getPlanDetails = (code) => {
    console.log(car_info);
    // let element = { name: code };
    // const exists = plans.some((item) => item.code === element.name);
    // console.log(exists);
    // if (
    //   car_info.make !== null &&
    //   car_info.model !== null &&
    //   car_info.year !== null &&
    //   car_info.fuel !== null &&
    //   car_info.value !== null &&
    //   car_info.mobile !== null
    // ) {
    // }
    // let element = { name: code };
    // const exists = plans.some((item) => item.code === element.name);
    // // if(exists && car_info.length>0 )
    // console.log(car_info);
  };

  useEffect(() => {
    getCarInfo();
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    setTimeout(function () {
      getPlanDetails(decryptData(props.match.params.details));
    }, 300);

    // eslint-disable-next-line
  }, [car_info]);

  const accordion = [
    { text: "Summary" },
    { text: "Benefits" },
    { text: "Details" },
  ];

  return (
    <>
      <div className="parent">
        <div className="main">
          <Navigation />
          <section className="quote-display">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-6 col-12 mt-10">
                  <br />
                  <Link to="/car-insurance">
                    <CornerUpLeft size={15} />
                    &nbsp;Change Insurer
                  </Link>
                </div>
              </div>
              <div className="row specific-quote">
                <div className="col-md-8 col-12">
                  <div className="quote-content py-2">
                    <div className="quote-intro p-3 mb-1">
                      <div className="intro-section logo">
                        <img src={apa} alt="" />
                      </div>
                      <div className="intro-section review">
                        <h6>Comprehensive Best Plan</h6>
                        <ul>
                          <li>
                            <Star
                              size={18}
                              style={{
                                color: "#ffdf00",
