export default (state, action) => {
  // console.log("state ", state);
  // console.log("action", action.type);
  // console.log("payload", action.payload);

  switch (action.type) {
    case "SET_GLOBAL_INFO":
      return {
        ...state,
        globals: action.payload,
      };
    case "SET_LOADING":
      return {
        ...state,
        isLoading: action.payload,
      };
    case "CHECK_USER":
      const { userchecked, updatepass } = action.payload;
      return {
        ...state,
        userCheck: userchecked,
        shouldChangePassword: updatepass,
      };
    case "LOG_STATUS":
      return {
        ...state,
        isLoggedin: action.payload,
      };
    case "LOG_OUT":
      return {
        ...state,
        isLoggedin: false,
        userCheck: false,
      };
    case "CHANGE_PASSWORD":
      return {
        ...state,
        shouldChangePassword: action.payload,
      };
    case "MESSAGE":
      return {
        ...state,
        message: action.payload,
      };
    case "GET_LEAVE_INFO":
      return {
        ...state,
        leave_info: action.payload,
      };
    case "GET_LEAVE_HISTORY":
      return {
        ...state,
        leave_history: action.payload,
      };
    case "GET_ALL_STAFF":
      return {
        ...state,
        staff: action.payload,
      };

    case "GET_NEXT_OF_KIN":
      return {
        ...state,
        next_of_kin: action.payload,
      };
    case "GET_LEAVE_APPROVALS":
      return {
        ...state,
        leave_approvals: action.payload,
      };
    case "GET_USER_INFO":
      return {
        ...state,
        user_info: action.payload,
      };
    case "SET_PRIVILEGE":
      return {
        ...state,
        privilege: action.payload,
      };
    case "GET_DEPENDENTS":
      return {
        ...state,
        dependant_info: action.payload,
      };
    case "GET_PAYSLIP":
      return {
        ...state,
        downloadSlip: action.payload,
      };
    default:
      return state;
  }
};
